  // ν”„λ΅μ νΈ λ°μ΄ν„°
  const projects = [
    {
      imgSrc: `${imageUrl}/sproutfarm.png`,
      title: 'SproutFarm',
      subtitle: 'μƒμ‹Ή λ†μ¥π±',
      description: 'μΊμ£Όμ–Ό ν”½μ…€ λ―Έλ‹κ²μ„',
      link: 'https://codingkirby.github.io/SproutFarm/'
    },
    {
      imgSrc: `${imageUrl}/purrfectday.png`,
      title: 'PurrFectDay.',
      subtitle: 'νΌν™λ°μ΄.',
      description: 'κ³ μ–‘μ΄μ™€ ν•¨κ» λ‚λ§μ λ°©μ„ λ§λ“¤μ–΄κ°€λ” κ·€μ—¬μ΄ ν¬λ‘λ¦¬μ¤νΈπ»',
      link: 'https://codingkirby.github.io/projects/PurrFectDay'
    },
    {
      imgSrc: `${imageUrl}/chatbuddy.png`,
      title: 'CHATBuddy',
      subtitle: 'μ±—λ²—π‘€',
      description: 'μ°μΈκ°μ„ μ§€λ‹ μ²­λ…„λ“¤μ„ μ„ν• AI μ±„ν… μ„λΉ„μ¤',
      link: 'https://codingkirby.github.io/projects/CHATBuddy'
